import React, { useEffect, useState } from "react";
import axios from "axios";
import { BsTrash } from "react-icons/bs";
import { InputGroup, FormControl, Button } from "react-bootstrap";
import "../ToDoList.css";

const TodoList = () => {
  const [todoList, setTodoList] = useState([]);
  const [inProgressList, setInProgressList] = useState([]);
  const [doneList, setDoneList] = useState([]);
  const [newTodo, setNewTodo] = useState("");
  const [newInProgress, setNewInProgress] = useState("");
  const [newDone, setNewDone] = useState("");
  const [additionalLists, setAdditionalLists] = useState([]);
  const [newListTitle, setNewListTitle] = useState("");

  useEffect(() => {
    fetchLists();
    fetchInProgressList();
    fetchDoneList();
    fetchAdditionalLists();
  }, []);

  const fetchLists = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/todos/getToDoList"
      );
      setTodoList(response.data);
    } catch (error) {
      console.error("Error fetching lists:", error);
    }
  };

  const fetchInProgressList = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/inprogress/getInProgressList"
      );
      setInProgressList(response.data);
    } catch (error) {
      console.error("Error fetching in-progress list:", error);
    }
  };

  const fetchDoneList = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/done/getDoneList"
      );
      setDoneList(response.data);
    } catch (error) {
      console.error("Error fetching done list:", error);
    }
  };

  const fetchAdditionalLists = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/additionalLists/getAdditionalLists"
      );
      setAdditionalLists(response.data);
    } catch (error) {
      console.error("Error fetching additional lists:", error);
    }
  };

  // Additional lists handlers
  const handleAddNewList = async () => {
    if (!newListTitle) return;
    try {
      const response = await axios.post(
        "http://localhost:5000/api/additionalLists/addAdditionalList",
        {
          title: newListTitle,
          items: [],
        }
      );
      setAdditionalLists([...additionalLists, response.data]);
      setNewListTitle("");
    } catch (error) {
      console.error("Error adding new list:", error);
    }
  };

  const handleAddNewListItem = async (listId, newItemTitle) => {
    try {
      const updatedLists = additionalLists.map((list) => {
        if (list._id === listId) {
          list.items.push({ title: newItemTitle });
        }
        return list;
      });
      await axios.put(
        `http://localhost:5000/api/additionalLists/updateAdditionalList/${listId}`,
        {
          items: updatedLists.find((list) => list._id === listId).items,
        }
      );
      setAdditionalLists(updatedLists);
    } catch (error) {
      console.error("Error adding item to list:", error);
    }
  };

  const handleDeleteNewListItem = async (listId, itemIndex) => {
    try {
      const updatedLists = additionalLists.map((list) => {
        if (list._id === listId) {
          list.items.splice(itemIndex, 1);
        }
        return list;
      });
      await axios.put(
        `http://localhost:5000/api/additionalLists/updateAdditionalList/${listId}`,
        {
          items: updatedLists.find((list) => list._id === listId).items,
        }
      );
      setAdditionalLists(updatedLists);
    } catch (error) {
      console.error("Error deleting item from list:", error);
    }
  };

  return (
    <div className="todo-container">
      <h1 className="todo-header">Todo List</h1>
      <InputGroup className="mb-3">
        <FormControl
          placeholder="New List Title"
          value={newListTitle}
          onChange={(e) => setNewListTitle(e.target.value)}
        />
        <Button className="addListButton" onClick={handleAddNewList}>
          Add List
        </Button>
      </InputGroup>
      <div className="list-container">
        {additionalLists.map((list) => (
          <div key={list._id} className="additional-list">
            <h2>{list.title}</h2>
            <InputGroup className="mb-3">
              <FormControl
                placeholder={`Add new item to ${list.title}`}
                value={list.newItem}
                onChange={(e) =>
                  setAdditionalLists(
                    additionalLists.map((l) =>
                      l._id === list._id ? { ...l, newItem: e.target.value } : l
                    )
                  )
                }
              />
              <Button
                className="addButton"
                onClick={() => handleAddNewListItem(list._id, list.newItem)}
              >
                Add
              </Button>
            </InputGroup>
            <div className="additional-list-items">
              {list.items.map((item, index) => (
                <div key={index} className="additional-item">
                  <textarea
                    value={item.title}
                    onChange={(e) =>
                      setAdditionalLists(
                        additionalLists.map((l) =>
                          l._id === list._id
                            ? {
                                ...l,
                                items: l.items.map((i, idx) =>
                                  idx === index
                                    ? { ...i, title: e.target.value }
                                    : i
                                ),
                              }
                            : l
                        )
                      )
                    }
                  />
                  <div className="additional-icons">
                    <BsTrash
                      className="cursor-pointer"
                      onClick={() => handleDeleteNewListItem(list._id, index)}
                    />
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
