import React, { useEffect, useState } from "react";
import axios from "axios";
import { BsTrash } from "react-icons/bs";
import { InputGroup, FormControl, Button } from "react-bootstrap";
import "../ToDoList.css";
import { useDispatch, useSelector } from "react-redux";
import {
  setToDos,
  addToDo,
  addToDoItem,
  deleteToDoItem,
  updateToDoItem,
} from "../slices/toDoSlice";

const TodoList = () => {
  const [newListTitle, setNewListTitle] = useState("");
  const dispatch = useDispatch();
  const additionalLists = useSelector((state) => state.toDo);

  useEffect(() => {
    fetchAdditionalLists();
  }, []);

  const fetchAdditionalLists = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/additionalLists/getAdditionalLists"
      );
      dispatch(setToDos(response.data));
    } catch (error) {
      console.error("Error fetching additional lists:", error);
    }
  };

  const handleAddNewList = async () => {
    if (!newListTitle) return;
    try {
      const response = await axios.post(
        "http://localhost:5000/api/additionalLists/addAdditionalList",
        {
          title: newListTitle,
          items: [],
        }
      );
      dispatch(addToDo(response.data));
      setNewListTitle("");
    } catch (error) {
      console.error("Error adding new list:", error);
    }
  };

  const handleAddNewListItem = async (listId, newItemTitle) => {
    try {
      await axios.put(
        `http://localhost:5000/api/additionalLists/updateAdditionalList/${listId}`,
        {
          items: [
            ...additionalLists.find((list) => list._id === listId).items,
            { title: newItemTitle },
          ],
        }
      );
      dispatch(addToDoItem({ listId, item: { title: newItemTitle } }));
    } catch (error) {
      console.error("Error adding item to list:", error);
    }
  };

  const handleDeleteNewListItem = async (listId, itemIndex) => {
    try {
      const updatedItems = additionalLists
        .find((list) => list._id === listId)
        .items.filter((_, index) => index !== itemIndex);
      await axios.put(
        `http://localhost:5000/api/additionalLists/updateAdditionalList/${listId}`,
        {
          items: updatedItems,
        }
      );
      dispatch(deleteToDoItem({ listId, itemIndex }));
    } catch (error) {
      console.error("Error deleting item from list:", error);
    }
  };

  const handleUpdateListItem = async (listId, itemIndex, newTitle) => {
    try {
      await axios.put(
        `http://localhost:5000/api/additionalLists/updateAdditionalList/${listId}`,
        {
          items: additionalLists
            .find((list) => list._id === listId)
            .items.map((item, index) =>
              index === itemIndex ? { ...item, title: newTitle } : item
            ),
        }
      );
      dispatch(updateToDoItem({ listId, itemIndex, newTitle }));
    } catch (error) {
      console.error("Error updating item in list:", error);
    }
  };

  return (
    <div className="todo-container">
      <h1 className="todo-header">Todo List</h1>
      <InputGroup className="mb-3">
        <FormControl
          placeholder="New List Title"
          value={newListTitle}
          onChange={(e) => setNewListTitle(e.target.value)}
        />
        <Button className="addListButton" onClick={handleAddNewList}>
          Add List
        </Button>
      </InputGroup>
      <div className="list-container">
        {additionalLists.map((list) => (
          <div key={list._id} className="additional-list">
            <h2>{list.title}</h2>
            <InputGroup className="mb-3">
              <FormControl
                placeholder={`Add new item to ${list.title}`}
                value={list.newItem || ""}
                onChange={(e) =>
                  dispatch(
                    updateToDoItem({
                      listId: list._id,
                      itemIndex: -1,
                      newTitle: e.target.value,
                    })
                  )
                }
              />
              <Button
                className="addButton"
                onClick={() =>
                  handleAddNewListItem(list._id, list.newItem || "")
                }
              >
                Add
              </Button>
            </InputGroup>
            <div className="additional-list-items">
              {list.items.map((item, index) => (
                <div key={index} className="additional-item">
                  <textarea
                    value={item.title}
                    onChange={(e) =>
                      handleUpdateListItem(list._id, index, e.target.value)
                    }
                  />
                  <div className="additional-icons">
                    <BsTrash
                      className="cursor-pointer"
                      onClick={() => handleDeleteNewListItem(list._id, index)}
                    />
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
