import React, { useEffect, useState } from "react";
import axios from "axios";
import { BsTrash, BsPencilSquare } from "react-icons/bs"; // Import Bootstrap icons
import { InputGroup, FormControl, Button } from "react-bootstrap"; // Import Bootstrap components

const TodoList = () => {
  const [toDoList, setToDoList] = useState([]);
  const [newToDo, setNewToDo] = useState("");

  useEffect(() => {
    const fetchToDoList = async () => {
      try {
        const response = await axios.get("http://localhost:5000/getToDoList");
        console.log(response.data);
        setToDoList(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchToDoList();
  }, []);

  const handleAddToDo = async () => {
    try {
      const response = await axios.post("http://localhost:5000/addToDo", {
        title: newToDo,
      });
      console.log("ToDo added:", response.data);
      setToDoList([...toDoList, response.data]);
      setNewToDo(""); // Clear input field after adding ToDo
    } catch (error) {
      console.error("Error adding ToDo:", error);
    }
  };

  const handleDeleteToDo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/deleteToDo/${id}`);
      console.log("ToDo deleted:", id);
      setToDoList(toDoList.filter((toDo) => toDo._id !== id));
    } catch (error) {
      console.error("Error deleting ToDo:", error);
    }
  };

  return (
    <div>
      <h1>ToDo List</h1>
      <InputGroup className="mb-3">
        <FormControl
          placeholder="Add new ToDo"
          value={newToDo}
          onChange={(e) => setNewToDo(e.target.value)}
        />
        <Button variant="primary" onClick={handleAddToDo}>
          Add
        </Button>
      </InputGroup>
      <div>
        {toDoList.map((toDo) => (
          <div key={toDo._id} className="d-flex align-items-center mb-2">
            <h5>{toDo.title}</h5>
            <BsTrash
              className="ml-2 text-danger cursor-pointer"
              onClick={() => handleDeleteToDo(toDo._id)}
            />
            <BsPencilSquare className="ml-2 text-primary cursor-pointer" />
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
