import React, { useEffect, useState } from "react";
import axios from "axios";
import { BsTrash } from "react-icons/bs";
import { InputGroup, FormControl, Button } from "react-bootstrap";
import "../ToDoList.css";

const TodoList = () => {
  const [list, setList] = useState([]);
  const [newItem, setNewItem] = useState("");

  useEffect(() => {
    fetchList();
  }, []);

  const fetchList = async () => {
    try {
      const response = await axios.get("http://localhost:5000/getList");
      setList(response.data);
    } catch (error) {
      console.error("Error fetching list:", error);
    }
  };

  const handleAddItem = async () => {
    try {
      const response = await axios.post("http://localhost:5000/addItem", {
        title: newItem,
      });
      setList([...list, response.data]);
      setNewItem("");
    } catch (error) {
      console.error("Error adding item:", error);
    }
  };

  const handleDeleteItem = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/deleteItem/${id}`);
      setList(list.filter((item) => item._id !== id));
    } catch (error) {
      console.error("Error deleting item:", error);
    }
  };

  const handleUpdateItem = async (id, updatedTitle) => {
    try {
      const updatedDate = new Date().toLocaleString();
      await axios.put(`http://localhost:5000/updateItem/${id}`, {
        title: updatedTitle,
        createdAt: updatedDate,
      });
      setList(
        list.map((item) =>
          item._id === id
            ? { ...item, title: updatedTitle, createdAt: updatedDate }
            : item
        )
      );
    } catch (error) {
      console.error("Error updating item:", error);
    }
  };

  const handleInlineEdit = async (id, updatedTitle) => {
    try {
      handleUpdateItem(id, updatedTitle);
    } catch (error) {
      console.error("Error updating item:", error);
    }
  };

  return (
    <div className="todo-container">
      <h1 className="todo-header">ToDo List</h1>
      <div className="list-container">
        <div className="todo-list">
          <h2>Items</h2>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Add new item"
              value={newItem}
              onChange={(e) => setNewItem(e.target.value)}
            />
            <Button className="addButton" onClick={handleAddItem}>
              Add
            </Button>
          </InputGroup>
          <div className="todo-list-items">
            {list.map((item) => (
              <div key={item._id} className="todo-item">
                <textarea
                  value={item.title}
                  onChange={(e) => handleInlineEdit(item._id, e.target.value)}
                />
                <p className="date-time">
                  {new Date(item.createdAt).toLocaleString()}
                </p>
                <div className="todo-icons">
                  <BsTrash
                    className="cursor-pointer"
                    onClick={() => handleDeleteItem(item._id)}
                  />
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TodoList;
