import React, { useEffect, useState } from "react";
import axios from "axios";
import { BsTrash, BsPencilSquare } from "react-icons/bs";
import { InputGroup, FormControl, Button } from "react-bootstrap";
import "../ToDoList.css";

const TodoList = () => {
  const [toDoList, setToDoList] = useState([]);
  const [inProgressList, setInProgressList] = useState([]);
  const [doneList, setDoneList] = useState([]);
  const [newToDo, setNewToDo] = useState("");

  useEffect(() => {
    fetchToDoLists();
  }, []);

  const fetchToDoLists = async () => {
    try {
      const response = await axios.get("http://localhost:5000/getToDoLists");
      const { toDo, inProgress, done } = response.data;
      setToDoList(toDo);
      setInProgressList(inProgress);
      setDoneList(done);
    } catch (error) {
      console.error("Error fetching ToDo lists:", error);
    }
  };

  const handleAddToDo = async () => {
    try {
      const response = await axios.post("http://localhost:5000/addToDo", {
        title: newToDo,
      });
      setToDoList([...toDoList, response.data]);
      setNewToDo("");
    } catch (error) {
      console.error("Error adding ToDo:", error);
    }
  };

  const handleDeleteToDo = async (id, listType) => {
    try {
      await axios.delete(`http://localhost:5000/deleteToDo/${id}`);
      switch (listType) {
        case "todo":
          setToDoList(toDoList.filter((toDo) => toDo._id !== id));
          break;
        case "inProgress":
          setInProgressList(inProgressList.filter((item) => item._id !== id));
          break;
        case "done":
          setDoneList(doneList.filter((item) => item._id !== id));
          break;
        default:
          break;
      }
    } catch (error) {
      console.error("Error deleting ToDo:", error);
    }
  };

  const handleUpdateToDo = async (id, updatedTitle, listType) => {
    try {
      const response = await axios.put(
        `http://localhost:5000/updateToDo/${id}`,
        {
          title: updatedTitle,
        }
      );
      switch (listType) {
        case "todo":
          setToDoList(toDoList.map((toDo) => (toDo._id === id ? response.data : toDo)));
          break;
        case "inProgress":
          setInProgressList(inProgressList.map((item) => (item._id === id ? response.data : item)));
          break;
        case "done":
          setDoneList(doneList.map((item) => (item._id === id ? response.data : item)));
          break;
        default:
          break;
      }
    } catch (error) {
      console.error("Error updating ToDo:", error);
    }
  };

  return (
    <div className="todo-container">
      <h1 className="todo-header">ToDo List</h1>
      <InputGroup className="mb-3">
        <FormControl
          placeholder="Add new ToDo"
          value={newToDo}
          onChange={(e) => setNewToDo(e.target.value)}
        />
        <Button className="addButton" onClick={handleAddToDo}>
          Add
        </Button>
      </InputGroup>
      <div className="todo-lists">
        <div className="todo-list">
          <h2>TO DO</h2>
          {toDoList.map((toDo) => (
            <div key={toDo._id} className="todo-item">
              <h3>{toDo.title}</h3>
              <p className="date-time">{new Date(toDo.createdAt).toLocaleString()}</p>
              <div className="todo-icons">
                <BsTrash className="cursor-pointer" onClick={() => handleDeleteToDo(toDo._id, "todo")} />
                <BsPencilSquare
                  className="cursor-pointer"
                  onClick={() => {
                    const updatedTitle = prompt("Enter new title:", toDo.title);
                    if (updatedTitle !== null) {
                      handleUpdateToDo(toDo._id, updatedTitle, "todo");
                    }
                  }}
                />
              </div>
            </div>
          ))}
        </div>
        <div className="todo-list">
          <h2>IN PROGRESS</h2>
          {inProgressList.map((item) => (
            <div key={item._id} className="todo-item">
              <h3>{item.title}</h3>
              <p className="date-time">{new Date(item.createdAt).toLocaleString()}</p>
              <div className="todo-icons">
                <BsTrash className="cursor-pointer" onClick={() => handleDeleteToDo(item._id, "inProgress")} />
                <BsPencilSquare
                  className="cursor-pointer"
                  onClick={() => {
                    const updatedTitle = prompt("Enter new title:", item.title);
                    if (updatedTitle !== null) {
                      handleUpdateToDo(item._id, updatedTitle, "inProgress");
                    }
                  }}
                />
              </div>
            </div>
          ))}
        </div>
        <div className="todo-list">
          <h2>DONE</h2>
          {doneList.map((item) => (
            <div key={item._id} className="todo-item">
              <h3>{item.title}</h3>
              <p className="date-time">{new Date(item.createdAt).toLocaleString()}</p>
              <div className="todo-icons">
                <BsTrash className="cursor-pointer" onClick={() => handleDeleteToDo(item._id, "done")} />
                <BsPencilSquare
                  className="cursor-pointer"
                  onClick={() => {
                    const updatedTitle = prompt("Enter new title:", item.title);
                    if (updatedTitle !== null) {
                      handleUpdateToDo(item._id, updatedTitle, "done");
                    }
                  }}
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TodoList;
