import React, { useEffect, useState } from "react";
import axios from "axios";
import { BsTrash, BsPencilSquare } from "react-icons/bs";
import { InputGroup, FormControl, Button } from "react-bootstrap";
import "../ToDoList.css"; 

const TodoList = () => {
  const [toDoList, setToDoList] = useState([]);
  const [newToDo, setNewToDo] = useState("");

  useEffect(() => {
    fetchToDoList();
  }, []);

  const fetchToDoList = async () => {
    try {
      const response = await axios.get("http://localhost:5000/getToDoList");
      setToDoList(response.data);
    } catch (error) {
      console.error("Error fetching ToDo list:", error);
    }
  };

  const handleAddToDo = async () => {
    try {
      const response = await axios.post("http://localhost:5000/addToDo", {
        title: newToDo,
        completed: false,
      });
      setToDoList([...toDoList, response.data]);
      setNewToDo("");
    } catch (error) {
      console.error("Error adding ToDo:", error);
    }
  };

  const handleDeleteToDo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/deletToDoList/${id}`);
      setToDoList(toDoList.filter((toDo) => toDo._id !== id));
    } catch (error) {
      console.error("Error deleting ToDo:", error);
    }
  };

  const handleUpdateToDo = async (id, updatedTitle) => {
    try {
      const response = await axios.put(
        `http://localhost:5000/updateToDoList/${id}`,
        {
          title: updatedTitle,
        }
      );
      const updatedToDoList = toDoList.map((toDo) =>
        toDo._id === id ? response.data : toDo
      );
      setToDoList(updatedToDoList);
    } catch (error) {
      console.error("Error updating ToDo:", error);
    }
  };

  const handleToggleComplete = async (id) => {
    const toDo = toDoList.find((toDo) => toDo._id === id);
    try {
      const response = await axios.put(
        `http://localhost:5000/updateToDoList/${id}`,
        {
          completed: !toDo.completed,
        }
      );
      const updatedToDoList = toDoList.map((toDo) =>
        toDo._id === id ? response.data : toDo
      );
      setToDoList(updatedToDoList);
    } catch (error) {
      console.error("Error toggling ToDo complete:", error);
    }
  };

  return (
    <div className="todo-container">
      <h1 className="todo-header">ToDo List</h1>
      <InputGroup className="mb-3">
        <FormControl
          placeholder="Add new ToDo"
          value={newToDo}
          onChange={(e) => setNewToDo(e.target.value)}
        />
        <Button className="addButton" onClick={handleAddToDo}>
          Add
        </Button>
      </InputGroup>
      <div className="todo-list">
        {toDoList.map((toDo) => (
          <div key={toDo._id} className="todo-item">
            <input
              type="checkbox"
              checked={toDo.completed}
              onChange={() => handleToggleComplete(toDo._id)}
            />
            <h2 className={toDo.completed ? "completed" : ""}>{toDo.title}</h2>
            <div className="todo-icons">
              <BsTrash
                className="cursor-pointer"
                onClick={() => handleDeleteToDo(toDo._id)}
              />
              <BsPencilSquare
                className="cursor-pointer"
                onClick={() => {
                  const updatedTitle = prompt("Enter new title:", toDo.title);
                  if (updatedTitle !== null) {
                    handleUpdateToDo(toDo._id, updatedTitle);
                  }
                }}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
