import React, { useEffect, useState } from "react";
import axios from "axios";
import { BsTrash } from "react-icons/bs";
import { InputGroup, FormControl, Button } from "react-bootstrap";
import "../ToDoList.css";

const TodoList = () => {
  const [todoList, setTodoList] = useState([]);
  const [inProgressList, setInProgressList] = useState([]);
  const [doneList, setDoneList] = useState([]);
  const [newTodo, setNewTodo] = useState("");
  const [newInProgress, setNewInProgress] = useState("");
  const [newDone, setNewDone] = useState("");
  const [additionalLists, setAdditionalLists] = useState([]);
  const [newListTitle, setNewListTitle] = useState("");

  useEffect(() => {
    fetchLists();
    fetchInProgressList();
    fetchDoneList();
  }, []);

  const fetchLists = async () => {
    try {
      const todoResponse = await axios.get("http://localhost:5000/getToDoList");
      setTodoList(todoResponse.data);
    } catch (error) {
      console.error("Error fetching lists:", error);
    }
  };

  const fetchInProgressList = async () => {
    try {
      const inProgressResponse = await axios.get(
        "http://localhost:5000/getInProgressList"
      );
      setInProgressList(inProgressResponse.data);
    } catch (error) {
      console.error("Error fetching in-progress list:", error);
    }
  };

  const fetchDoneList = async () => {
    try {
      const doneResponse = await axios.get("http://localhost:5000/getDoneList");
      setDoneList(doneResponse.data);
    } catch (error) {
      console.error("Error fetching done list:", error);
    }
  };

  const handleAddTodo = async () => {
    try {
      const response = await axios.post("http://localhost:5000/addToDo", {
        title: newTodo,
      });
      setTodoList([...todoList, response.data]);
      setNewTodo("");
    } catch (error) {
      console.error("Error adding todo:", error);
    }
  };

  const handleInProgress = async () => {
    try {
      const inProgressResponse = await axios.post(
        "http://localhost:5000/addInProgressList",
        { InProgresstitle: newInProgress }
      );
      setInProgressList([...inProgressList, inProgressResponse.data]);
      setNewInProgress("");
    } catch (error) {
      console.error("Error adding in-progress item:", error);
    }
  };

  const handleAddDone = async () => {
    try {
      const response = await axios.post("http://localhost:5000/addDoneList", {
        DoneTitle: newDone,
      });
      setDoneList([...doneList, response.data]);
      setNewDone("");
    } catch (error) {
      console.error("Error adding done item:", error);
    }
  };

  const handleDeleteTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/deletToDoList/${id}`);
      setTodoList(todoList.filter((todo) => todo._id !== id));
    } catch (error) {
      console.error("Error deleting todo:", error);
    }
  };

  const handleDeleteInProgress = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/deletInProgressList/${id}`);
      setInProgressList(inProgressList.filter((item) => item._id !== id));
    } catch (error) {
      console.error("Error deleting in-progress item:", error);
    }
  };

  const handleDeleteDone = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/deletDoneList/${id}`);
      setDoneList(doneList.filter((item) => item._id !== id));
    } catch (error) {
      console.error("Error deleting done item:", error);
    }
  };

  const handleUpdateTodo = async (id, updatedTitle) => {
    try {
      const updatedDate = new Date().toLocaleString();
      await axios.put(`http://localhost:5000/updateToDoList/${id}`, {
        title: updatedTitle,
        createdAt: updatedDate,
      });
      setTodoList(
        todoList.map((todo) =>
          todo._id === id
            ? { ...todo, title: updatedTitle, createdAt: updatedDate }
            : todo
        )
      );
    } catch (error) {
      console.error("Error updating todo:", error);
    }
  };

  const handleUpdateInProgress = async (id, updatedProgressTitle) => {
    try {
      const updatedDate = new Date().toLocaleString();
      await axios.put(`http://localhost:5000/updateInProgressList/${id}`, {
        InProgresstitle: updatedProgressTitle,
        createdAt: updatedDate,
      });
      setInProgressList(
        inProgressList.map((item) =>
          item._id === id
            ? {
                ...item,
                InProgresstitle: updatedProgressTitle,
                createdAt: updatedDate,
              }
            : item
        )
      );
    } catch (error) {
      console.error("Error updating in-progress item:", error);
    }
  };

  const handleUpdateDone = async (id, updatedDoneTitle) => {
    try {
      const updatedDate = new Date().toLocaleString();
      await axios.put(`http://localhost:5000/updateDoneList/${id}`, {
        DoneTitle: updatedDoneTitle,
        createdAt: updatedDate,
      });
      setDoneList(
        doneList.map((item) =>
          item._id === id
            ? { ...item, DoneTitle: updatedDoneTitle, createdAt: updatedDate }
            : item
        )
      );
    } catch (error) {
      console.error("Error updating done item:", error);
    }
  };

  const handleInlineEdit = async (listType, id, updatedTitle) => {
    try {
      switch (listType) {
        case "todo":
          handleUpdateTodo(id, updatedTitle);
          break;
        case "inProgress":
          handleUpdateInProgress(id, updatedTitle);
          break;
        case "done":
          handleUpdateDone(id, updatedTitle);
          break;
        default:
          break;
      }
    } catch (error) {
      console.error("Error updating item:", error);
    }
  };

  const handleAddNewList = () => {
    setAdditionalLists([
      ...additionalLists,
      { id: Date.now(), title: newListTitle, newItem: "", items: [] },
    ]);
    setNewListTitle("");
  };

  const handleAddNewListItem = (listId, newItemTitle) => {
    setAdditionalLists(
      additionalLists.map((list) =>
        list.id === listId
          ? { ...list, items: [...list.items, { title: newItemTitle }] }
          : list
      )
    );
  };

  const handleDeleteNewListItem = (listId, itemIndex) => {
    setAdditionalLists(
      additionalLists.map((list) =>
        list.id === listId
          ? {
              ...list,
              items: list.items.filter((_, index) => index !== itemIndex),
            }
          : list
      )
    );
  };

  return (
    <div className="todo-container">
      <h1 className="todo-header">ToDo List</h1>
      <div className="list-container">
        <div className="todo-list">
          <h2>TODO</h2>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Add new Todo"
              value={newTodo}
              onChange={(e) => setNewTodo(e.target.value)}
            />
            <Button className="addButton" onClick={handleAddTodo}>
              Add
            </Button>
          </InputGroup>
          <div className="todo-list-items">
            {todoList.map((todo) => (
              <div key={todo._id} className="todo-item">
                <textarea
                  value={todo.title}
                  onChange={(e) =>
                    handleInlineEdit("todo", todo._id, e.target.value)
                  }
                />
                <p className="date-time">
                  {new Date(todo.createdAt).toLocaleString()}
                </p>
                <div className="todo-icons">
                  <BsTrash
                    className="cursor-pointer"
                    onClick={() => handleDeleteTodo(todo._id)}
                  />
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="inprogress-list">
          <h2>IN PROGRESS</h2>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Add new in-progress"
              value={newInProgress}
              onChange={(e) => setNewInProgress(e.target.value)}
            />
            <Button className="addButton" onClick={handleInProgress}>
              Add
            </Button>
          </InputGroup>
          <div className="progress-list-items">
            {inProgressList.map((inProgress) => (
              <div key={inProgress._id} className="progress-item">
                <textarea
                  value={inProgress.InProgresstitle}
                  onChange={(e) =>
                    handleInlineEdit(
                      "inProgress",
                      inProgress._id,
                      e.target.value
                    )
                  }
                />
                <p className="date-time">
                  {new Date(inProgress.createdAt).toLocaleString()}
                </p>
                <div className="progress-icons">
                  <BsTrash
                    className="cursor-pointer"
                    onClick={() => handleDeleteInProgress(inProgress._id)}
                  />
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="done-list">
          <h2>DONE</h2>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Add new done"
              value={newDone}
              onChange={(e) => setNewDone(e.target.value)}
            />
            <Button className="addButton" onClick={handleAddDone}>
              Add
            </Button>
          </InputGroup>
          <div className="done-list-items">
            {doneList.map((done) => (
              <div key={done._id} className="done-item">
                <textarea
                  value={done.DoneTitle}
                  onChange={(e) =>
                    handleInlineEdit("done", done._id, e.target.value)
                  }
                />
                <p className="date-time">
                  {new Date(done.createdAt).toLocaleString()}
                </p>
                <div className="done-icons">
                  <BsTrash
                    className="cursor-pointer"
                    onClick={() => handleDeleteDone(done._id)}
                  />
                </div>
              </div>
            ))}
          </div>
        </div>
        {additionalLists.map((list) => (
          <div key={list.id} className="additional-list">
            <h2>{list.title}</h2>
            <InputGroup className="mb-3">
              <FormControl
                placeholder={`Add new item to ${list.title}`}
                value={list.newItem}
                onChange={(e) =>
                  setAdditionalLists(
                    additionalLists.map((l) =>
                      l.id === list.id ? { ...l, newItem: e.target.value } : l
                    )
                  )
                }
              />
              <Button
                className="addButton"
                onClick={() => handleAddNewListItem(list.id, list.newItem)}
              >
                Add
              </Button>
            </InputGroup>
            <div className="additional-list-items">
              {list.items.map((item, index) => (
                <div key={index} className="additional-item">
                  <textarea
                    value={item.title}
                    onChange={(e) =>
                      setAdditionalLists(
                        additionalLists.map((l) =>
                          l.id === list.id
                            ? {
                                ...l,
                                items: l.items.map((i, idx) =>
                                  idx === index
                                    ? { ...i, title: e.target.value }
                                    : i
                                ),
                              }
                            : l
                        )
                      )
                    }
                  />
                  <div className="additional-icons">
                    <BsTrash
                      className="cursor-pointer"
                      onClick={() => handleDeleteNewListItem(list.id, index)}
                    />
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
      <InputGroup className="mb-3">
        <FormControl
          placeholder="New List Title"
          value={newListTitle}
          onChange={(e) => setNewListTitle(e.target.value)}
        />
        <Button className="addListButton" onClick={handleAddNewList}>
          Add List
        </Button>
      </InputGroup>
    </div>
  );
};

export default TodoList;
